mapper.select:
SELECT

FROM MP_YY_REPLY
WHERE PUBLIC_CODE = #{publicCode,jdbcType=VARCHAR}
AND
REPLY_TYPE = #{replyType,jdbcType=INTEGER}

mapper.update:
update MP_YY_REPLY
set CONTENT = #{content,jdbcType=VARCHAR}
where REPLY_CODE = #{replyCode,jdbcType=VARCHAR}

update MP_YY_REPLY
set CONTENT = #{content,jdbcType=VARCHAR}
where PUBLIC_CODE = #{publicCode,jdbcType=VARCHAR}
and REPLY_TYPE = #{replyType,jdbcType=INTEGER}

update MP_YY_REPLY
set REPLY_FLAG = #{replyFlag,jdbcType=INTEGER}
where REPLY_CODE = #{replyCode,jdbcType=VARCHAR}

update MP_YY_REPLY
set REPLY_FLAG = #{replyFlag,jdbcType=INTEGER}
where PUBLIC_CODE = #{publicCode,jdbcType=VARCHAR}
and REPLY_TYPE = #{replyType,jdbcType=INTEGER}

update MP_YY_REPLY
set PUBLIC_CODE = #{publicCode,jdbcType=VARCHAR},
  REPLY_TYPE = #{replyType,jdbcType=INTEGER},
  CONTENT = #{content,jdbcType=VARCHAR},
  REPLY_FLAG = #{replyFlag,jdbcType=INTEGER}
where REPLY_CODE = #{replyCode,jdbcType=VARCHAR}
